import React, {
  Component
} from 'react';
import axios from "axios"
import logo from './logo.svg';
import './App.css';

class App extends Component {

  state = {
    email: '',
    emailError: ''
  }

  handleChange = e => {
    if (e.target.value.length === 0) this.setState({
      emailError: 'Field should not be empty',
      email: e.target.value
    })
    else if (e.target.value.length < 10) this.setState({
      emailError: 'Email length more than 10 chars',
      email: e.target.value
    })
    else this.setState({
      email: e.target.value,
      emailError: ''
    })
  }

  handleSubmit = () => {
    console.log(this.state.emailError !== '')
    if (this.state.emailError !== '') {
      window.alert('some erros')
    } else this.sumbitRequest()
  }

  sumbitRequest = () => {

    axios.post('https://jsonplaceholder.typicode.com/posts', {
        title: this.state.email,
        body: 'bar',
        userId: 1
      })
      .then(data => console.log(data.json()))
      .catch(e => console.log('error : ', e))
    console.log('submiting request')
  }

  render() {

    const {
      email,
      emailError
    } = this.state
    return ( <
      div className = "App" >
      <
      header className = "App-header" >

      <
      img src = {
        logo
      }
      className = "App-logo"
      alt = "logo" / >
      <
      p >
      Edit < code > src / App.js < /code> and save to reload. <
      /p> <
      input className = {
        emailError ? 'inputError' : ''
      }
      onChange = {
        this.handleChange
      }
      value = {
        email
      }
      placeholder = "email" / > {
        emailError && < p className = 'errorMessage' > {
          emailError
        } < /p> } <
        button onClick = {
          this.handleSubmit
        } > Submit form < /button> <
        a
        className = "App-link"
        href = "https://reactjs.org"
        target = "_blank"
        rel = "noopener noreferrer" >
        Learn React <
        /a> <
        /header> <
        /div>
      );
    }
  }

  export default App;
